import { useState } from 'react';
import './App.css'

const getFilesFromEvent = event => {
    return Array.prototype.slice.call(event.target.files);
}

const MAX_COUNT = 5;

function App() {

	const [uploadedFiles, setUploadedFiles] = useState([])
    const [limit, setLimit] = useState(false);


    const handleUploadFiles = files => {
        const uploaded = [...uploadedFiles];
        let limitExceeded = false;
        files.forEach((file) => {
            if (uploaded.findIndex((f) => f.name === file.name) === -1) {
                uploaded.push(file);
                if (uploaded.length === 3) setLimit(true);
                if (uploaded.length > MAX_COUNT) {
                    alert(`You can only add a maximum of ${MAX_COUNT} files`);
                    setLimit(false);
                    limitExceeded = true;
                    return;
                }
            }

            // handleFileEvent(file)
        })
        if (!limitExceeded) setUploadedFiles(uploaded)

    }

    const handleFileEvent =  (e) => {
        // const chosenFiles =  getFilesFromEvent(e);
        const chosenFiles = Array.prototype.slice.call(e.target.files)
        handleUploadFiles(chosenFiles);
    }
	return (
		<div className="App">

			<input id='fileUpload' type='file' multiple
					accept='application/pdf, image/png'
                    onChange={handleFileEvent}
                    // disabled={limit}
			/>
			<label htmlFor='fileUpload'>
				<a  className="btn btn-primary">Upload Files</a>
			</label>

			<div className="uploaded-files-list">
				{uploadedFiles.map(file => (
                    <div >
                        {file.name}
                    </div>
                ))}
			</div>

		</div>
	);
}

export default App;
