import './App.css'

const getFilesFromEvent = event => {
    return Array.prototype.slice.call(event.target.files);
}

function App() {

	const [uploadedFiles, setUploadedFiles] = useState([])


    const handleUploadFiles = files => {
        files.forEach((file) => {
            // handleFileEvent(file)
            const uploaded = [...uploadFiles];
    let limitExceeded = false;
    // console.log(files)
    files.forEach(async (file, i) => {
      //const { name, size, type, lastModified } = file;

      // Check if file already exists
      if (uploaded.findIndex((f) => f.name === file.name) === -1) {
        uploaded.push(file);

        if (uploaded.length === 3) setLimit(true);
        if (uploaded.length > MAX_SIZE.count) {
          // console.log('limit reached')
          alert('You can only add a maximum of three files');
          setLimit(false);
          limitExceeded = true;
          return;
        }
      }
    });
    if (!limitExceeded) setUploadFiles(uploaded);
        })
    }

    const handleFileEvent = async (e) => {
        const chosenFiles = await getFilesFromEvent(e);
        handleUploadFiles(chosenFiles)
    }
	return (
		<div className="App">

			<input id='fileUpload' type='file' multiple
					accept='application/pdf, image/png'
                    onChange={async e => {
                        handleFileEvent(e);

                        e.target.value = null;
                    }}  
			/>
			<label htmlFor='fileUpload'>
				<a  className="btn btn-primary">Upload Files</a>
			</label>

			<div className="uploaded-files-list">
				{uploadedFiles.map(file => (
                    <div>
                        {file.name}
                    </div>
                ))}
			</div>

		</div>
	);
}

export default App;
